transforms.result:
.concept {
  padding: 50px 40px;

  > .title {
    display: flex;
    flex-flow: column;
    text-align: center;

    > .top {
      position: relative;
      margin: auto;

      &::after {
        content: "";
        display: inline-block;
        position: absolute;
        width: 110px;
        height: 4px;
        bottom: -10px;
        background-color: $blue-line;
        left: calc(50% - 55px);
      }
    }

    > .detail {
      margin: 40px 0 60px;
      line-height: 2;
    }
  }

  > .list {
    display: flex;
    flex-flow: column;
    margin: 0 150px;
    list-style: none;
    gap: 40px;

    > .item:nth-child(2n) {
      display: flex;
      padding: 20px 20px 20px 0;
      background-color: $lite-blue;
      border-style: solid;
      border-color: $black;
      border-width: 2px;
      border-radius: 10px;
      box-shadow: 10px 10px 6px rgba(0, 0, 0, 0.3);
    }

    > .item:nth-child(2n-1) {
      display: flex;
      padding: 20px 0 20px 20px;
      background-color: $lite-blue;
      border-style: solid;
      border-color: $black;
      border-width: 2px;
      border-radius: 10px;
      box-shadow: 10px 10px 6px rgba(0, 0, 0, 0.3);
    }
  }
}

.text {
  text-align: center;

  > .title {
    display: inline-block;
    text-align: center;
    background: linear-gradient(transparent 40%, $yellow 80%);
  }

  > .explanation {
    display: flex;
    height: 80%;
    align-items: center;
    text-align: start;
    line-height: 2rem;
    margin: auto 60px;
  }
  > .title-detail {
    display: flex;
    height: 80%;
    align-items: center;
    margin: auto 20px;
  }
}

.strong-point {
