/*矢印と下線の形状*/
.staff {
  position: absolute;
  background-color: $white;
  right: 0;
  :hover {
    cursor: pointer;
  }

  > .btnarrow4 {
    font-weight: bold;
    font-size: 20px;
    /*矢印と下線の基点とするためrelativeを指定*/
    position: relative;
    /*形状*/
    display: inline-block;
    padding: 0 20px;
    color: #333;
    text-decoration: none;
    outline: none;
    background-color: $white;
    border: 0;

    &::before {
      content: "";
      /*絶対配置で下線の位置を決める*/
      position: absolute;
      bottom: -8px;
      left: 10px;
      /*下線の形状*/
      width: 200px;
      height: 2px;
      background: $blue-line;
      /*アニメーションの指定*/
      transition: all 0.3s;
    }

    &::after {
      content: "";
      /*絶対配置で矢印の位置を決める*/
      position: absolute;
      bottom: 3px;
      right: -34px;
      /*矢印の形状*/
      width: 30px;
      height: 2px;
      background: $blue-line;
      transform: rotate(45deg);
      /*アニメーションの指定*/
      transition: all 0.3s;
    }

    /*hoverした際の移動*/
    &:hover::before {
      left: 21px;
    }

    &:hover::after {
      right: -45px;
    }
  }
}

.request {
  z-index: 1;
}

.contact {
  position: fixed;
  bottom: 30px;
  right: 20px;
  display: flex;
  justify-content: center;
  align-items: center;
